mapper.select:
SELECT COUNT(*) FROM user_recharge
WHERE user_id = #{userId}
and order_status = #{status}
and add_time > date_sub(now(), interval 1 hour)

SELECT

FROM user_recharge

  user_id = #{uid}

    and pay_channel = #{payChannel}


    and order_status = #{orderStatus}


ORDER BY id DESC

  SELECT

  FROM user_recharge
where  agent_id != 1

      and ( agent_id = #{agentId}
      or agent_id in (
      select agent_user.id
      FROM agent_user
      where parent_id = ${agentId}
      )
      )


      and user_id = #{userId}


      and nick_name like CONCAT('%','${realName}','%')


      and order_status = #{state}


      and user_id = (select user.id from user where phone = #{phone})


      and pay_time >= #{begin_time}


      and pay_time <= #{end_time}



    and add_time >= #{applyBeginTime}


    and add_time <= #{applyEndTime}

  ORDER BY id DESC

SELECT

FROM user_recharge

  agent_id = #{searchId}

    and nick_name like CONCAT('%','${realName}','%')


    and pay_channel = #{payChannel}


    and order_status = #{state}


ORDER BY id DESC
